import {
    EVENTS_CREATE_FAIL,
    EVENTS_CREATE_REQUEST,
    EVENTS_CREATE_SUCCESS,
    EVENTS_DELETE_FAIL,
    EVENTS_DELETE_REQUEST,
    EVENTS_DELETE_SUCCESS,
    EVENTS_LIST_FAIL,
    EVENTS_LIST_REQUEST,
    EVENTS_LIST_SUCCESS,
    EVENTS_UPDATE_FAIL,
    EVENTS_UPDATE_REQUEST,
    EVENTS_UPDATE_SUCCESS,
} from "../constants/notesContants";
import axios from "axios";

export const listNotes = () => async ( dispatch, getState ) => {
    try {
        dispatch( {
            type: EVENTS_LIST_REQUEST,
        } );

        const {
            userLogin: { userInfo },
        } = getState();

        const config = {
            headers: {
                Authorization: `Bearer ${ userInfo.token }`,
            },
        };

        const { data } = await axios.get( `/api/events`, config );

        dispatch( {
            type: EVENTS_LIST_SUCCESS,
            payload: data,
        } );
    } catch ( error ) {
        const message =
            error.response && error.response.data.message
                ? error.response.data.message
                : error.message;
        dispatch( {
            type: EVENTS_LIST_FAIL,
            payload: message,
        } );
    }
};

export const createNoteAction = ( title, content, category ) => async (
    dispatch,
    getState
) => {
    try {
        dispatch( {
            type: EVENTS_CREATE_REQUEST,
        } );

        const {
            userLogin: { userInfo },
        } = getState();

        const config = {
            headers: {
                "Content-Type": "application/json",
                Authorization: `Bearer ${ userInfo.token }`,
            },
        };

        const { data } = await axios.post(
            `/api/events/create`,
            { title, content, category },
            config
        );

        dispatch( {
            type: EVENTS_CREATE_SUCCESS,
            payload: data,
        } );
    } catch ( error ) {
        const message =
            error.response && error.response.data.message
                ? error.response.data.message
                : error.message;
        dispatch( {
            type: EVENTS_CREATE_FAIL,
            payload: message,
        } );
    }
};

export const deleteNoteAction = ( id ) => async ( dispatch, getState ) => {
    try {
        dispatch( {
            type: EVENTS_DELETE_REQUEST,
        } );

        const {
            userLogin: { userInfo },
        } = getState();

        const config = {
            headers: {
                Authorization: `Bearer ${ userInfo.token }`,
            },
        };

        const { data } = await axios.delete( `/api/events/${ id }`, config );

        dispatch( {
            type: EVENTS_DELETE_SUCCESS,
            payload: data,
        } );
    } catch ( error ) {
        const message =
            error.response && error.response.data.message
                ? error.response.data.message
                : error.message;
        dispatch( {
            type: EVENTS_DELETE_FAIL,
            payload: message,
        } );
    }
};

export const updateNoteAction = ( id, title, content, category ) => async (
    dispatch,
    getState
) => {
    try {
        dispatch( {
            type: EVENTS_UPDATE_REQUEST,
        } );

        const {
            userLogin: { userInfo },
        } = getState();

        const config = {
            headers: {
                "Content-Type": "application/json",
                Authorization: `Bearer ${ userInfo.token }`,
            },
        };

        const { data } = await axios.put(
            `/api/events/${ id }`,
            { title, content, category },
            config
        );

        dispatch( {
            type: EVENTS_UPDATE_SUCCESS,
            payload: data,
        } );
    } catch ( error ) {
        const message =
            error.response && error.response.data.message
                ? error.response.data.message
                : error.message;
        dispatch( {
            type: EVENTS_UPDATE_FAIL,
            payload: message,
        } );
    }
};
