    };
    return (
        <MainScreen title='Welcome back'>
            <Link to='/createnote'>
                <Button
                    styles={ { marginLeft: 10, marginBottom: 6 } }
                    size='lg'
                >
                    Create New Note
                </Button>
            </Link>
            { notes.map( ( note ) => {
                return <Accordion key={ note._id }>
                    <Card style={ { margin: 10 } }>
                        <Card.Header style={ { display: 'flex' } }>
                            <span style={ {
                                color: 'black',
                                textDecoration: 'none',
                                flex: 1,
                                cursor: 'pointer',
                                alignSelf: 'center',
                                fontSize: 18,

                            } }
                            >
                                <Accordion.Toggle
                                    as={ Link }
                                    variant='link'
                                    eventKey='0'
                                >
                                    { note.title }
                                </Accordion.Toggle>
                            </span>
                            <div>
                                <Button
                                    href={ `/note/${ note._id }` }
                                >
                                    Edit
                                </Button>
                                <Button
                                    className='mx-2'
                                    variant='danger'
                                    onClick={ () => deleteHandler( note._id ) }
                                >
                                    Delete</Button>
                            </div>
                        </Card.Header>
                        <Accordion.Collapse eventKey='0'>
                            <Card.Body>
                                <h4>
                                    <Badge variant='success'>Category - { note.category }</Badge>
                                </h4>
                                <blockquote className="blockquote mb-0">
                                    <p>
                                        { note.content }
                                    </p>
                                    <footer className="blockquote-footer">
                                        Created On - date
                                    </footer>
                                </blockquote>
                            </Card.Body>
                        </Accordion.Collapse>
                    </Card>
                </Accordion>;
            } ) }
        </MainScreen >
    );
};

export default MyNotes;
